mapper.update:
UPDATE APPLICATION
SET DELFLAG ='Y'
WHERE SEQ = #{seq}

mapper.insert:
INSERT INTO CAREER (
	SEQ, AP_SEQ, CORPNAME,
	HIREDATE, FIREDATE, EMPLOY,
	"POSITION", "WORK"
)
VALUES(
	CAREER_SEQ.NEXTVAL, #{ap_seq}, #{corpname},
	#{hiredate}, #{firedate}, #{employ},
	#{position}, #{work}
)

mapper.update:
UPDATE CAREER
SET 
	CORPNAME = #{corpname}, HIREDATE = #{hiredate}, FIREDATE = #{firedate},
	EMPLOY = #{employ}, "POSITION" = #{position}, "WORK" = #{work}
WHERE SEQ = #{seq}

